<?php/** * Base add action */// -----------------------------------------------------------------------------$errors = array();$values = array();// Form is sendif (isset($_POST['send'])){	if (empty($_POST['title']))	{		$errors[] = 'Поле "Заголовок" не заполнено.';	}		// Check errors	if (empty($errors))	{		$modx->db->insert(array(			'`title`' => esc($_POST['title']),			'`desc`' => esc($_POST['desc'])		), $mod_table[0]);				redirect();		exit;	}	else	{		$values = $_POST;	}}// -----------------------------------------------------------------------------$errors = implode('<br>', $errors);// Load formrequire $mod_path . 'inc/tinymce.inc.php';require $mod_path . 'form/base.form.php';